import React from "react";
import { FaPenNib } from "react-icons/fa";

function Blog() {
  return (
    <div className="p-12">
      <h2 className="text-3xl font-bold mb-10 text-blue-400 flex items-center gap-2">
        <FaPenNib /> Mahek's Reflections
      </h2>

      {[
        {
          title: "Confidence is a lie. Courage isn’t.",
          text: "Most people wait until they feel confident. But confidence is the result, not the requirement. Courage is what moves you forward. Confidence shows up after."
        },
        {
          title: "Belonging isn’t just a DEI checkbox.",
          text: "It’s whether your silence is noticed. Whether your name is respected. Whether your ideas are followed up on. The best organizations build cultures, not policies."
        },
        {
          title: "Not feeling Indian enough or American enough.",
          text: "Third-culture identity means always translating. But I’ve learned I don’t have to choose sides. I can shape a new space. One that makes room for all of me."
        },
        {
          title: "Peer mentorship as real leadership.",
          text: "Leadership isn’t always positional. It’s who you check in on. Who you coach gently. Who you stand beside. Some of the most powerful mentoring happens quietly."
        },
        {
          title: "Unlearning 'strong means silent.'",
          text: "I used to think strength meant never asking for help. Now I know vulnerability is strength. Collaboration is power. Asking for what you need takes courage."
        }
      ].map((post, index) => (
        <section key={index} className="mb-12">
          <h3 className="text-xl font-semibold text-blue-300 mb-2">{post.title}</h3>
          <p className="text-lg leading-relaxed">{post.text}</p>
        </section>
      ))}
    </div>
  );
}

export default Blog;
